import React, { useState, useEffect } from 'react';
import { BrowserRouter, Route, Routes, useLocation, useNavigate } from "react-router-dom";
import { CSSTransition, TransitionGroup } from 'react-transition-group';
import StartPage from './components/StartPage';
import Survey from "./components/Survey";
import './css/common.css';
import { createTheme, ThemeProvider } from '@mui/material/styles';

// axios 추가
import axios from 'axios';

const theme = createTheme({
  palette: {
    primary: {
      main: '#1976d2',
      light: '#42a5f5',
      dark: '#1565c0',
      contrastText: '#fff',
    },
    ochre: {
      main: '#E3D026',
      light: '#E9DB5D',
      dark: '#A29415',
      contrastText: '#242105',
    },
    babyblue: {
        main: '#a7caed',
        light: '#d1edf6',
        dark: '#75a8da',
        contrastText: '#ffffff',
    },
    pupleblue: {
        main: '#4A3AFF',
        light: '#90b7ff',
        dark: '#1d11a9',
        contrastText: '#fff',
    }
  },
});

function App() {

    const location = useLocation(); // 현재 위치 가져오기
    const navigate = useNavigate(); // 페이지 이동을 위한 navigate 함수
    const [isDark, setIsDark] = useState(false); // 배경 어두운 상태 관리

    const handleButtonClick = (path) => {
        setIsDark(true); // 배경 어둡게 설정
        setTimeout(() => {
            navigate(path); // 페이지 전환
            setIsDark(false); // 배경 밝게 설정
        }, 300); // 애니메이션 시간과 일치하도록 지연
    };

    const baseUrl = "http://localhost:8080";

    const [ data, setData ] = useState();

    useEffect(() => {
        putSpringData();
    },[])

    async function putSpringData() {
        await axios
            .get(baseUrl + "/exampleImage")
            .then((res)=>{
                console.log(res.data);
                setData(res.data);
            })
            .catch((err)=>{
                console.log(err);
            })
    }

    return (
        <TransitionGroup>
            <CSSTransition
                key={location.key}
                classNames="fade"
                timeout={300}
            >
                <div className="App">
                    {isDark && <div className="full-screen" />} {/* 배경 어두운 상태일 때만 표시 */}
                    <ThemeProvider theme={theme}>
                        <Routes location={location}>
                            <Route exact path="/" element={<StartPage onClick={() => handleButtonClick('/survey')} />} />
                            <Route path="/startPage" element={<StartPage onClick={() => handleButtonClick('/survey')} />} />
                            <Route path="/survey" element={<Survey />} />
                        </Routes>
                    </ThemeProvider>
                </div>
            </CSSTransition>
        </TransitionGroup>
    );
}

const WrappedApp = () => (
    <BrowserRouter>
        <App />
    </BrowserRouter>
);

export default WrappedApp;
