                        <span className="description-span">비즈니스에 대한 상세한 설명을 작성해주세요.</span>
                        <textarea ref={inputRef}
                                  value={formValues.step3} // 상태에 저장된 값
                                  onChange={handleInputChange} // 값이 변경될 때 상태 업데이트
                                  onKeyUp={handleInputKeyDown}
