import * as React from 'react';
import Button from '@mui/material/Button';
import Box from '@mui/material/Box';
import TextField from '@mui/material/TextField';
import Stack from '@mui/material/Stack';
import { useTheme } from '@mui/material/styles';
import Stepper from '@mui/material/Stepper';
import Step from '@mui/material/Step';
import StepLabel from '@mui/material/StepLabel';
import Typography from '@mui/material/Typography';
import {Link} from "react-router-dom";

function Survey() {
  const theme = useTheme();
  const [activeStep, setActiveStep] = React.useState(0);
  const [formValues, setFormValues] = React.useState({
    step0: '',
    step1: '',
    step2: '',
    step3: ''
  }); // 각 스텝의 입력 값을 저장할 상태
  const inputRef = React.useRef(null);
  const steps = ['brandName', 'brandNameMean', 'business', 'vibe', 'selectImage'];

  const handleNext = () => {
    setActiveStep((prevActiveStep) => prevActiveStep + 1);
    console.log(formValues);
  };

  const handleBack = () => {
    setActiveStep((prevActiveStep) => prevActiveStep - 1);
  };

  React.useEffect(() => {
    // activeStep이 변경될 때마다 실행, input 요소에 포커스 설정
    if (inputRef.current) {
        inputRef.current.focus();
    }
  }, [activeStep]); // activeStep이 변경될 때마다 실행

  // 입력 값이 변경될 때 상태 업데이트
  const handleInputChange = (e) => {
    setFormValues({
      ...formValues,
      [`step${activeStep}`]: e.target.value
    });
  };

  const renderStep = () => {
    let content = null;
    switch (activeStep) {
        case 0:
            content = (
                <div className="p-lr-8">
                    <h1 className="m-b-0">브랜드명</h1>
                    <span className="description-span">CI /BI 에 적용될 브랜드명 또는 서비스명을 입력해주세요.</span>
                    <input type="text"
                           ref={inputRef}
                           value={formValues.step0} // 상태에 저장된 값
                           onChange={handleInputChange} // 값이 변경될 때 상태 업데이트
                           name="brandName" className="common-input" placeholder="예시) Brand Logo Lab" required/>
                </div>
            );
            break;
        case 1:
            content = (
                <div className="p-lr-8">
                    <h1 className="m-b-0">브랜드명 의미</h1>
                    <span className="description-span">브랜드명 또는 서비스명의 의미를 입력해주세요.</span>
                    <input type="text"
                           ref={inputRef}
                           value={formValues.step1} // 상태에 저장된 값
                           onChange={handleInputChange} // 값이 변경될 때 상태 업데이트
                           name="brandNameMean" className="common-input" placeholder="예시) Brand Logo를 만드는 연구실(Lab)" required/>
                </div>
            );
            break;
        case 2:
            content = (
                <div className="p-lr-8">
                    <h1 className="m-b-0">비즈니스</h1>
                    <span className="description-span">비즈니스에 대한 상세한 설명을 작성해주세요.</span>
                    <textarea ref={inputRef}
                              value={formValues.step2} // 상태에 저장된 값
                              onChange={handleInputChange} // 값이 변경될 때 상태 업데이트
                              name="business" className="common-text" rows="3" placeholder="예시) 브랜드 로고, 서비스 로고를 생성형 AI를 통해 빠르고 정확한, 감각의 디자인을 개발합니다." required>
          </textarea>
                </div>
            );
            break;
        case 3:
            content = (
                <div className="p-lr-8">
                    <h1 className="m-b-0">로고 분위기</h1>
                    <span className="description-span">원하는 분위기부터 담고 싶은 이미지, 강조하고 싶은 주제 등 상세히 작성해주세요.</span>
                    <textarea ref={inputRef}
                              value={formValues.step3} // 상태에 저장된 값
                              onChange={handleInputChange} // 값이 변경될 때 상태 업데이트
                              name="vibe" className="common-text" rows="3" placeholder="예시) 클래식한, 심플한, 개성있는, 집, 나무, 자연의 이미지 등" required>
          </textarea>
                </div>
            );
            break;
        case 4:
            content = (
                <div className="p-8">
                    <div className="m-8">원하는 스타일을 선택해주세요!</div>
                    <Stack direction="row">
                    </Stack>
                </div>
            );
            break;
        default:
            content = <div>Error</div>;
            break;
    }
    return content;
  };

  return (
      <div className="parent">
          <div className="child">
              <div className="survey text-center">
                  <div className="progress-div">
                      <Stepper activeStep={activeStep}>
                          {steps.map((label, index) => {
                              return (
                                  <Step key={label}>
                                      <StepLabel></StepLabel>
                                  </Step>
                              );
                          })}
                      </Stepper>
                  </div>
                  <div className="content-div text-left">
                      {activeStep === steps.length ? (
                          <React.Fragment>
                              생성중이에요, 잠시만 기다려주세요 !
                          </React.Fragment>
                      ) : (
                          <React.Fragment>
                              <Typography sx={{ mt: 2, mb: 1 }}>{renderStep()}</Typography>
                          </React.Fragment>
                      )}
                  </div>
                  <div className="button-div">
                      {activeStep < steps.length && (
                          <Box sx={{ display: 'flex', flexDirection: 'row', pt: 2 }}>
                              {activeStep > 0 && (
                                  <Button
                                      className="common-button back-button" variant="contained"
                                      disabled={activeStep === 0}
                                      onClick={handleBack}
                                      sx={{ mr: 1 }}
                                      >
                                      BACK
                                  </Button>
                              )}
                              <Box sx={{ flex: '1 1 auto' }} />
                              <Button className="common-button f-right" variant="contained" color="pupleblue" onClick={handleNext}>
                                  {activeStep === steps.length - 1 ? 'FINISH' : 'NEXT'}
                              </Button>
                          </Box>
                      )}
                  </div>
              </div>
          </div>
      </div>
  );
}

export default Survey;
